mapper.select:
select
id, product_id, product_count, start_time, end_time, company, phone, ispay, isdel
from layui.order


        and id = #{id}


        and product_id = #{productId}


        and product_count = #{productCount}


        and start_time = #{startTime}


        and end_time = #{endTime}


        and company = #{company}


        and phone = #{phone}


        and ispay = #{ispay}


        and isdel = #{isdel}
